/**
 * @desc Get hash from an url.
 * @returns Array
 */
function getHash(href){
	if (!href){
		return null;
	}
	if (href.indexOf('#')!=-1){
		return null;
	}

	var path=href.split("//", 2);
	if (!path || path.length!=2){
		return;
	}
	var sub_path=path[1];
	var url=sub_path.split("/");
	if (!url || url.length<2){
		return;
	}
	url=AP.array.sub(url, 1);
	return url;
};


