	$.fn.rposition=function(target){
		if (!$(this).closest(target).length){
			return {top:0, left: 0};
		}
		
		var $e=$(this);
		var top=0;
		var left=0;
		
		while (true){
			if (!$e.length){
				break;
			}
			
			if ($e.is(target)){
				break;
			}
			
			var p=$e.position();
			
			if ($e.css("position")=="static" || !$e.css("position")){
			}else{
				top+=p.top;
				left+=p.left;	
			}
			
			$e=$e.parent();
		}
		
		return {top:top, left: left};
	};
	
