	this.inspect=function(url){
		var parts= url.split("://");

		if (parts.length==2){
			if (isBaseURL(parts[0])){
				var apps=parts[0].split("-",2);
				if (apps.length!=2){
					return errorURL();
				}
				
				var app=apps[1];
				var local=0;
				
				if (Client.base.app && app.toLowerCase()==Client.base.app.toLowerCase()){
					local=1;
				}
				
				
				// @desc Special handling, when base link is referenced from outside
				if (!local && (app=="message" || app == "msg")){
					if (AP.rewriteCheck(parts[1])){
						return {	
							"base": 1,
							"global":0,
							"local": 1,
							"app": apps[1],
							"url": Base.domain(Client.base.app)+"/"+parts[1],
							"abs": 1,
							"raw":url
						}
					}
					
					
					return {	
						"base": 1,
						"global":1,
						"local": 0,
						"app": apps[1],
						"url": Base.domain("message")+"/"+parts[1],
						"abs": 1,
						"raw":url
					}
				}
				
				
				if (local){
					var curl=parts[1];
					if (app!="message"){
						curl=parts[1];
					}
					
					return {	
						"base": 1,
						"global":1,
						"local": local,
						"app": apps[1],
						"url": curl,
						"abs": 1,
						"raw":url
					}
				}else{
					return {	
						"base": 1,
						"global":0,
						"local": local,
						"app": apps[1],
						"url": this.domain(apps[1])+"/"+parts[1],
						"abs": 1,
						"raw":url
					}
				}
			}else{
				return {
					"base": 0,
					"app": "none",
					"url": url,
					"abs":1
				}
			}
		}else if (parts.length==1){
			return {	
				"base": 1,
				"global": ((Client.base.app.toLowerCase()=="message" || Client.base.app.toLowerCase()=="msg")?1:0),
				"local": 1,
				"app": Client.base.app.toLowerCase(),
				"url": url,
				"abs": 1,
				"raw":url
			}
		}else{
			return {	
				"base": 0,
				"app": "none",
				"url": url,
				"abs": 0
			}
		}
	};
	
	
