/**
 * @desc Procedural calls of lang.
 * @param key
 * @param flag
 */
function lang(key, flag){
	return key;
	
	if (!flag){
		flag=null;
	}
	var msg=Lang.message(key);
	if (flag=='ucf'){
		return AP.W.ucf(msg);
	}
	if (flag=='ucw'){
		return AP.W.ucw(msg);
	}
	if (flag=='upper'){
		return AP.W.upper(msg);
	}
	return msg;
};

