UI.range = function (start, end, label) {
	var d = [{value: 0, "label": label}];
	if (start < end) {
		for (var i = start; i <= end; i++) {
			d.push({value: i, "label": i});
		}
	} else {
		for (var i = start; i >= end; i--) {
			d.push({value: i, "label": i});
		}
	}
	return d;
};


