	function calculateSectors(data) {
		var sectors = [];
		var l = data.size / 2
		var a = 0 // Angle
		var aRad = 0 // Angle in Rad
		var z = 0 // Size z
		var x = 0 // Side x
		var y = 0 // Side y
		var X = 0 // SVG X coordinate
		var Y = 0 // SVG Y coordinate
		var R = 0 // Rotation

		data.sectors.map(function(item, key) {
			a = 360 * item.percentage;
			aCalc = ( a > 180 ) ? 360 - a : a;
			aRad = aCalc * Math.PI / 180;
			z = Math.sqrt( 2*l*l - ( 2*l*l*Math.cos(aRad) ) );
			if( aCalc <= 90 ) {
				x = l*Math.sin(aRad);
			}
			else {
				x = l*Math.sin((180 - aCalc) * Math.PI/180 );
			}
			
			y = Math.sqrt( z*z - x*x );
			Y = y;

			if( a <= 180 ) {
				X = l + x;
				arcSweep = 0;
			}
			else {
				X = l - x;
				arcSweep = 1;
			}

			sectors.push({
				percentage: item.percentage,
				label: item.label,
				arcSweep: arcSweep,
				L: l,
				X: X,
				Y: Y,
				R: R,
				color: item.color
			});

			R = R + a;
		})


		return sectors
	};
