	function loadFilterFromURL(url){
		var r=[];
		var parts=AP.word.split("&",url);
		for (var i=0; i<parts.length; i++){
			var ps=AP.word.split("=", parts[i]);
			if (ps.length!=2){
				continue;
			}
			
			var key=ps[0];
			var val=ps[1];
			
			if (AP.word.suffix(key,"_min")){
				$.log([key, val]);	
			}
			
			if (AP.word.suffix(key,"_min")){
				key=key.substr(0, key.length-4);
				r.push({
					key: key,
					operator: "min",
					value: val
				});
			}else if (AP.word.suffix(key,"_max")){
				key=key.substr(0, key.length-4);
				r.push({
					key: key,
					operator: "max",
					value: val
				});
			}else{
				r.push({
					key: key,
					operator: "match",
					value: val
				});
			}
		}
		
		return r;
	};
	
