Side.milestone=new function __SideMilestone(){
	this.init=function(){
		if (!Client.pageData.milestones){
			return;
		}
		
		Client.pageData.milestones.sort(function(e, f){
			return parseInt(f.time)-parseInt(e.time);
		});
		
		var html=AP.render(Client.pageData.milestones, function(e){
			return getHTML(e);
		});
		
		$("#js-side-milestones").html("<div class='side-milestones'>"+(html)+"</div>");
	};


	this.update=function(code){
		var milestone = AP.array.findObj(Client.pageData.milestones, code.task.milestone_id);
		if (milestone) {
			milestone.total = milestone.total + 1;
		}
		Side.milestone.init();
	};


	this.removeTask=function(code){
		var milestone = AP.array.findObj(Client.pageData.milestones, code.task.milestone_id);
		if (milestone) {
			milestone.total = milestone.total - 1;
		}
		Side.milestone.init();
	};
	
	
	function getHTML(e){
		if (!parseInt(e.id)){
			return '';
		}
		
		var ec='';
		if (parseInt(e.time)>AP.time.now()){
			ec='-active';
		}
		
		if (parseInt(e.time)<AP.time.now()-30*24*3600){
			ec='-old';
		}
		
		var u=People.get(e.user_id);

		var user_html='';
		var user_info='';
		if (u && parseInt(u.uid)){
			user_html="<div class='avatar'><img src='"+(AP.xthumb(u.gavatar))+"'/></div>";
			user_info="<span class='url username'>"+(u.name)+"</span> &nbsp;&middot;&nbsp;";
		}
		
		return "<div class='milestone "+(ec)+"' data-id='"+(e.id)+"' title='"+(e.name)+" - "+(AP.i18n.date(e.time))+"'> <div class='status-icon'></div> <div class='mdate'>"+(AP.time.time('<em>%v</em><span>%d/%m</span>', e.time))+"</div> <div class='name ap-xdot url' data-url='milestone/"+(e.id)+"'>"+(e.name)+"</div> <div class='subtitle'> "+(user_info)+"<em>"+(e.done)+"/"+(e.total)+"</em> </div> <div class='bar hidden'> <div class='inner'> <div class='c' style='width:"+(e.complete)+"%'> <div class='candy -small -black'></div> </div> <div class='info'> <em>"+(e.done)+"/"+(e.total)+"</em> &mdash; <em>"+(parseFloat(e.complete).toFixed(1))+"</em>% <div class='extra'>"+(AP.time.time('%v, %d/%m/%Y', e.time))+"</div> </div> </div> </div> <div class='mng -cmenuw'> <span class='-ap icon-dots-two-horizontal'></span> <div class='-cmenu -no-icon -padding'> <div class='-item' onclick='Milestone.edit("+(e.id)+")'>Chnh sa</div> <div class='-item' onclick='Milestone.remove("+(e.id)+")'>Xa</div> </div> </div> </div>";
	};
	
};
