			Live.post("notifications.get", {last_update: 0}, function(data){
				// $(ref).ajaxOn();
				if (!data.params){
					return;
				}
				
				N.ui.last_load=AP.time.now();
				N.ui.has_new=false;
				clear_badge();
				
				if (data.params.load_more){
					N.ui.displayAppend(data.rows);
					if (!data.rows.length){
						N.ui.showNoMore();
					}
				}else{
					if (data.rows && data.rows.length){
						N.ui.display(data.rows);
					}else{
						N.ui.showNoMore();
					}
				}
			});
