	function moveElement($item, dir){
		if (dir=="up"){
			var $x=$item.prev();
			if (!$x.length || !$x.hasClass("item")){
				return;
			}
			$x.insertAfter($item);
		}else{
			var $x=$item.next();
			if (!$x.length || !$x.hasClass("item")){
				return;
			}
			$x.insertBefore($item);
		}
	};
